import React from 'react'
import { Link } from 'react-router-dom'
export default function CategoriesGrid(){
  const cats = [
    {id:'clothing', name:'Clothing', img:'https://via.placeholder.com/400x300?text=Clothing'},
    {id:'carrybags', name:'Eco Carrybags', img:'https://via.placeholder.com/400x300?text=Bags'},
    {id:'new', name:'New Arrivals', img:'https://via.placeholder.com/400x300?text=New'},
    {id:'limited', name:'Limited Drops', img:'https://via.placeholder.com/400x300?text=Limited'},
  ]
  return (
    <section className="container mx-auto px-4 py-8">
      <h3 className="text-xl font-semibold mb-4 text-slate-800">Shop by category</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {cats.map(cat => (
          <Link to={`/category/${cat.id}`} key={cat.id} className="group block rounded-lg overflow-hidden shadow-soft hover:shadow-md transform hover:-translate-y-1 transition">
            <div className="relative h-40 bg-gray-100">
              <img src={cat.img} alt={cat.name} className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"/>
            </div>
            <div className="p-4 bg-white">
              <div className="text-lg font-medium text-slate-900">{cat.name}</div>
              <div className="text-sm text-muted mt-1">Curated selection</div>
            </div>
          </Link>
        ))}
      </div>
    </section>
  )
}
